global $lccharts_scripts;
 
$lccharts_scripts = array();


function lc_charts_insert_js($js) {
  global $lccharts_scripts;
  if ( !in_array( $js, $lccharts_scripts ) ) {
    $lccharts_scripts[] = $js;
  }
}



